import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import styled from "styled-components";
import { Tablet, Desktop } from "../Responsive";
import { Data } from "../config/data";

const Wrapper = styled.div`
  margin: 0;
  padding: 0;
  font-family: "Poppins", sans-serif;

  .post-container {
    display: grid;
    grid-template-columns: 100%;
    gap: 10px;
    padding: 20px;
    ${Tablet({ gridTemplateColumns: "33% 33% 33%" })}
    ${Desktop({ gridTemplateColumns: "25% 25% 25%", gap: "20px" })}
  }

  .post {
    /* height: 250px; */
    padding: 10px;
    box-shadow: 0 5px 5px #888888;
  }

  .img-container {
    width: 100%;
    height: 200px;
    background-color: grey;
    margin: auto;
  }
  img {
    height: 100%;
    width: 100%;
    object-fit: cover;
  }
  h4 {
    font-family: Georgia, "Times New Roman", Times, serif;
    font-size: 24px;
    margin-top: 10px;
    font-weight: 600;
  }
  p {
    font-size: 16px;
    font-weight: 500;
  }
  span {
    color: var(--primary-color);
    font-weight: 500;
  }
`;

function Posts() {
  const [posts, setPosts] = useState([]);

  const getPosts = async () => {
    try {
      const data = await axios.get(`/post`);
      setPosts(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getPosts();
  }, []);

  const items = posts.map((item, i) => (
    <div className="post" key={item._id}>
      <Link
        style={{ textDecoration: "none", color: "inherit" }}
        to={`/post/${item._id}`}
      >
        <div className="img-container">
          <img src={item.cover} alt="product cover" width="100" height="100" />
        </div>
        <h4>{item.title}</h4>

        <div className="desc">
          <p>{item.description.slice(0, 100)}</p>
          <span>more</span>
        </div>
      </Link>
    </div>
  ));

  return (
    <Wrapper>
      <div className="post-container">{items}</div>
    </Wrapper>
  );
}

export default Posts;
