import React, { useState, useEffect } from "react";
import { Link, useParams, useNavigate } from "react-router-dom";
import styled from "styled-components";
import axios from "axios";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
// import { Tablet, Desktop } from "../../Responsive";

const Wrapper = styled.div`
  min-height: 50vh;
  margin: 0;
  padding: 0;
  font-family: "Poppins", sans-serif;
`;

function EditPostPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [cover, setCover] = useState(null);
  const [values, setValues] = useState({
    title: "",
    description: "",
    author: "",
    category: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setCover(selectedFile);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { title, category, description, author } = values;
    // Handle text and file uploads here
    const formData = new FormData();
    formData.append("title", title);
    formData.append("cover", cover);
    formData.append("category", category);
    formData.append("author", author);
    formData.append("description", description);

    try {
      const resp = await fetch(`/post/${id}`, {
        method: "PUT",
        body: formData,
      });
      //   console.log(resp.data);
      if (resp.status === 200) {
        // Success
        setMessage("post updated successfully");
        console.log("File uploaded successfully");
        navigate("/admin");
        navigate("/some-route", { state: { message } });
      } else {
        // Handle error
        setError("failed to update post");
        console.error("File upload failed");
      }
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setError(error.response.data);
      }
      console.log(error.message);
    }
  };
  const getPost = async () => {
    try {
      const resp = await axios.get(`/post/${id}`);
      setValues(resp.data);
      console.log(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getPost();
  }, [id]);

  const [selectedOption, setSelectedOption] = useState(true);

  // Function to handle the change in the selected option
  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value === "true");
  };

  return (
    <>
      <Navbar />
      <Wrapper>
        <div className="container-fluid py-4 my-0 text-white bg-dark">
          <div className="container">
            <h1 className="text-darks">Add Post</h1>
            <form onSubmit={handleSubmit}>
              {message !== "" && (
                <span className="alert alert-success">{message}</span>
              )}
              {error !== "" && (
                <span className="alert alert-danger">{error}</span>
              )}
              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="name">
                  Title
                </label>
                <input
                  type="text"
                  name="title"
                  placeholder="title"
                  className="form-control"
                  value={values.title}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="author">
                  Author
                </label>
                <input
                  type="text"
                  name="author"
                  placeholder="author"
                  className="form-control"
                  value={values.author}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="category">
                  Category
                </label>
                <input
                  name="category"
                  id="category"
                  placeholder="category"
                  className="form-control"
                  value={values.category}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="description">
                  Description
                </label>
                <textarea
                  className="form-control"
                  placeholder="description"
                  name="description"
                  id="description"
                  rows="5"
                  value={values.description}
                  onChange={handleChange}
                ></textarea>
              </div>
              <label>
                Select an option:
                <select
                  value={selectedOption.toString()}
                  onChange={handleOptionChange}
                >
                  <option value="true">True</option>
                  <option value="false">False</option>
                </select>
              </label>
              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="cover">
                  Post cover
                </label>
                <input
                  type="file"
                  name="cover"
                  placeholder="cover"
                  className="form-control"
                  onChange={handleFileChange}
                />
              </div>
              <button type="submit" className="btn btn-info text-light mt-3">
                Update
              </button>
            </form>
            <Link to="/admin" className="btn btn-danger mt-4">
              Close
            </Link>
          </div>
        </div>
      </Wrapper>
      <Footer />
    </>
  );
}

export default EditPostPage;
