import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import { Tablet } from "../Responsive";

const Wrapper = styled.div`
  padding: 0;
  margin: 0;

  .post {
    min-height: 70vh;
    display: flex;
    flex-direction: column;
    padding: 20px;
    ${Tablet({ flexDirection: "column" })}
  }
  .img-container,
  .content {
    flex: 100%;
    /* ${Tablet({ flexDirection: "column" })} */
  }
  .img-container {
    width: 500px;
    height: 600px;
  }
  img {
    width: 100%;
    height: 100%;
  }
  p {
    font-size: 10px;
    font-weight: 600;
  }
  .rating {
    display: flex;
    margin-top: 10px;
  }
  .rate {
    font-size: 8px;
    color: rgba(200, 80, 12, 1);
    margin-right: 5px;
  }
`;

function SinglePost() {
  const [post, setPost] = useState({});
  const { id } = useParams();

  const getPost = async () => {
    try {
      const resp = await fetch(`/post/${id}`);
      const data = await resp.json();
      setPost(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getPost();
  }, [id]);

  return (
    <Wrapper>
      <div className="post">
        <div className="img-container">
          <img src={post.image} alt={post.name} />
        </div>
        <div className="content">
          <h3>{post.name}</h3>
          <p className="price">${post.price}</p>
        </div>
      </div>
      <div className="related">
        <h4>related posts</h4>
        <div>the posts</div>
      </div>
    </Wrapper>
  );
}

export default SinglePost;
