import React from "react";
import { styled } from "styled-components";
import { Tablet, Desktop } from "../Responsive";

const Wrapper = styled`
    display: flex;
    color: white;
    justify-content: flex-end;
    padding-right: 20px;
    background-image: linear-gradient(to right, rgb(78, 59, 97), rgb(13, 128, 32),rgb(78, 59, 97),rgb(78, 59, 97));
    

.h-icon{
    padding-right: 5px;
}
.admin div{
    margin: 5px;
    ${Tablet({ margin: "10px" })}
}
.admin div a{
    text-decoration: none;
    color: inherit;
    font-size: 10px;
    font-weight: 500;
    ${Tablet({ fontSize: "14px" })}
}
.admin div a:hover{
color: rgb(177, 211, 84);
}
.bg-light{
    background-color: white !important;
    /* background-image: linear-gradient(to right, blue, rgb(155, 170, 240), blue) !important; */
    box-shadow: 5px 5px 5px 5px #eee6e6;
    
}
.nav-link, .navbar-brand{
  color: var(--primary-color);
  font-weight: 700;
  font-family: 'Times New Roman', Times, serif;
}
.nav-link:hover{
    color: rgb(177, 211, 84);
    }


`;

function Header({ user }) {
  return (
    <Wrapper>
      {user && user.isAdmin && (
        <>
          <div>
            <a href="/post/admin">
              <i className="fa-solid h-icon fa-user"></i>Admin
            </a>
          </div>
          <div>
            <a href="/auth/logout">
              <i className="h-icon"></i>Logout
            </a>
          </div>
        </>
      )}
      {user && (
        <div>
          <a href="/auth/logout">
            <i className="h-icon"></i>Logout
          </a>
        </div>
      )}
      {!user && (
        <>
          <div>
            <a href="/auth/register">
              <i className="fa-solid h-icon fa-book"></i>Sign Up
            </a>
          </div>
          <div>
            <a href="/auth/login">
              <i className="fa-solid h-icon fa-user"></i>Sign In
            </a>
          </div>
        </>
      )}
    </Wrapper>
  );
}

export default Header;
