import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { Tablet, Desktop } from "../Responsive";
import { Data } from "../config/data";

const Wrapper = styled.div`
  min-height: 50vh;
  margin: 0;
  padding: 0;
  font-family: "Poppins", sans-serif;

  h2 {
    font-family: "Lora", serif;
    font-style: normal;
    font-weight: 700;
    margin-top: 15px;
  }
  .featured .img-container {
    width: 100%;
    height: 400px;
    background-color: grey;
    margin: auto;
    ${Tablet({ width: "70%" })}
  }
  .featured img {
    height: 100%;
    width: 100%;
    object-fit: cover;
  }
  .featured h3 {
    font-family: Georgia, "Times New Roman", Times, serif;
    font-size: 30px;
    margin-top: 10px;
    font-weight: 700;
  }
`;

const Home = () => {
  // const location = useLocation();
  // const navigate = useNavigate();
  const featuredItems = Data.map((item, i) => (
    <div className="post" key={item._id}>
      <Link
        style={{ textDecoration: "none", color: "inherit" }}
        to={`/post/${item._id}`}
      >
        <div className="img-container">
          <img src={item.cover} alt="product cover" width="100" height="100" />
        </div>
        <h3>{item.title}</h3>
      </Link>
    </div>
  ));

  const otherItems = Data.map((item, i) => (
    <div className="post" key={item._id}>
      <Link
        style={{ textDecoration: "none", color: "inherit" }}
        to={`/post/${item._id}`}
      >
        <h4>{item.title}</h4>

        <div className="desc">
          <p>{item.description.slice(0, 100)}</p>
          <span>more</span>
        </div>
      </Link>
    </div>
  ));
  return (
    <>
      <Navbar />
      <Wrapper>
        <h2 className="">Welcome Home</h2>
        <div className="featured">{featuredItems}</div>
        <h2>Trending news</h2>
        <div className="others">{otherItems}</div>
      </Wrapper>
      <Footer />
    </>
  );
};

export default Home;
