import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import moment from "moment";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Tablet, Desktop } from "../../Responsive";
import { Data } from "../../config/data";

const Wrapper = styled.div`
  min-height: 50vh;
  margin: 0;
  padding: 0;
  font-family: "Poppins", sans-serif;
`;

const items = Data.map((post, i) => (
  <tr>
    <th scope="row">
      <img src={post.cover} alt="cover" height="40" width="40" />
    </th>
    <td colSpan="2">{post.title}</td>
    <td>
      <a className="btn btn-secondary" href="/post/update/<%= posts[i]._id %>">
        <i className="fa-regular fa-pen-to-square"></i>
      </a>
    </td>
    <td>
      <a className="btn btn-danger" href="/post/delete/<%= posts[i]._id %>">
        <i className="fa-solid fa-trash-can"></i>
      </a>
    </td>
    <td>{moment(post.createdAt).format("DD MMM YY")}</td>
  </tr>
));

function AdminPage() {
  return (
    <>
      <Wrapper>
        <div className="container adminpage mt-5">
          <h4 className="text-bold">Admin Page</h4>
          <div className=" my-3 bg-secondary">
            <div className="add">
              <a href="/post/create" className="btn btn-primary m-2">
                Add Post
              </a>
            </div>
          </div>
          <h3 className="text-info mt-4 mb-2">News Posts</h3>
          <table className="table table-dark table-striped post">
            <thead>
              <tr>
                <th scope="col">Cover</th>
                <th scope="col" colSpan="2">
                  Title
                </th>
                <th scope="col">Edit</th>
                <th scope="col">Delete</th>
                <th scope="col">Date</th>
              </tr>
            </thead>
            <tbody>{items}</tbody>
          </table>
        </div>
      </Wrapper>
    </>
  );
}

export default AdminPage;
