import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import { Tablet } from "../Responsive";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { Data } from "../config/data";

const Wrapper = styled.div`
  padding: 0;
  margin: 0;
  min-height: 50vh;
  font-family: "Poppins", sans-serif;

  .post {
    min-height: 70vh;
    display: flex;
    flex-direction: column;
    padding: 20px;
  }
  .img-container,
  .content {
  }
  .img-container {
    margin: 20px;
    margin-left: auto;
    margin-right: auto;
    width: 95%;
    height: 40vh;
    ${Tablet({ width: "90%", height: "45vh" })}
  }
  img {
    width: 100%;
    height: 100%;
  }
  p {
    font-size: 16px;
    line-height: 30px;
    /* font-weight: 600; */
  }
`;

function SinglePost() {
  // const [post, setPost] = useState({});
  const { id } = useParams();

  // const getPost = async () => {
  //   try {
  //     const resp = await fetch(`/post/${id}`);
  //     const data = await resp.json();
  //     setPost(data);
  //     console.log(data);
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  // useEffect(() => {
  //   getPost();
  //}, [id]);

  const post = Data.find((item) => item._id == id);
  console.log(post);

  return (
    <>
      <Navbar />
      <Wrapper>
        <div className="post">
          <h3>{post.title}</h3>
          <div className="img-container">
            <img src={post.cover} alt="post cover" />
          </div>
          <div className="content">
            <p className="price">${post.description}</p>
          </div>
        </div>
        <div className="related">
          <h4>More related posts from leaways blog</h4>
          <div>the posts</div>
        </div>
      </Wrapper>
      <Footer />
    </>
  );
}

export default SinglePost;
