import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import styled from "styled-components";
import axios from "axios";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
// import { Tablet, Desktop } from "../../Responsive";

const Wrapper = styled.div`
  min-height: 50vh;
  margin: 0;
  padding: 0;
  font-family: "Poppins", sans-serif;
`;

function EditPostPage() {
  const { id } = useParams();

  const getPost = async () => {
    try {
      const resp = await axios.get(`/post/${id}`);
      setPost(resp.data);
      console.log(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getPost();
  }, [id]);
  return (
    <>
      <Navbar />
      <Wrapper>
        <div className="container-fluid py-4 my-0 text-white bg-dark">
          <div className="container">
            <h1 className="text-darks">Add Post</h1>
            <form onSubmit={handleSubmit}>
              {/* {message !== "" && (
            <span className="alert alert-success">{message}</span>
          )} */}
              {error !== "" && (
                <span className="alert alert-danger">{error}</span>
              )}
              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="name">
                  Title
                </label>
                <input
                  type="text"
                  name="title"
                  placeholder="title"
                  className="form-control"
                  value={values.title}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="author">
                  Author
                </label>
                <input
                  type="text"
                  name="author"
                  placeholder="author"
                  className="form-control"
                  value={values.author}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="category">
                  Category
                </label>
                <input
                  name="category"
                  id="category"
                  placeholder="category"
                  className="form-control"
                  value={values.category}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="label">
                  Description
                </label>
                <textarea
                  className="form-control"
                  placeholder="description"
                  name="description"
                  id="description"
                  rows="5"
                  value={values.description}
                  onChange={handleChange}
                ></textarea>
              </div>
              <div className="form-group mb-3">
                <label className="mb-1" htmlFor="cover">
                  Post cover
                </label>
                <input
                  type="file"
                  name="cover"
                  placeholder="cover"
                  className="form-control"
                  value={values.cover}
                  onChange={handleChange}
                />
              </div>
              <button type="submit" className="btn btn-info text-light mt-3">
                Submit
              </button>
            </form>
            <Link to="/admin" className="btn btn-danger mt-4">
              Close
            </Link>
          </div>
        </div>
      </Wrapper>
      <Footer />
    </>
  );
}

export default EditPostPage;
